sp_widget.client_script:
function($scope,$http,spUtil) {
  /* widget controller */
  var c = this
	$scope.buttonText = 'Update Records';
	$scope.submitStatus = false;

	function toggle() {
		if ($scope.requisition != undefined) {
			$('#pogroup').show();
			$('#invoicegroup').show();
			$('#updatebtngroup').show();
		} else {
			$('#pogroup').hide();
			$('#invoicegroup').hide();
			$('#updatebtngroup').hide();			
		}
	} 

	$scope.submit = function() {
		var url = '/api/x_tjc_easy_asset/easyasset/update?' +
				'sys_id=' + $scope.requisition +
				'&po=' + $scope.ponumber +
				'&invoice=' + $scope.invoice;

		$scope.submitStatus = true;
		$scope.buttonText = 'Updating Records...';

		$http.patch(url).then(
			function(r){
				if (r.data.result.code != 'SUCCESS') {
					spUtil.addErrorMessage('Something went wrong: ' + r.data.result.code);
					console.log(r);
				} else {
					$scope.buttonText = 'Updating Complete!';
				}
			}, function(e){
				console.log(e);
				var errorMessage = 'Something went wrong!';
				var errorCode = '';

				if (e.status == 400) {errorCode = 'COAL'}
				if (e.status == 401) {errorCode = 'IRON'}
				if (e.status == 402) {errorCode = 'REDSTONE'}
				if (e.status == 403) {errorCode = 'DIAMONDS'}
				if (e.status == 404) {errorCode = 'VOID'}
				if (e.status == 405) {errorCode = 'LAPIS'}
				if (e.status == 406) {errorCode = 'SANDSTONE'}
				if (e.status == 408) {errorCode = 'GOLD'}
				if (e.status == 409) {errorCode = 'QUARTZ'}

				spUtil.addErrorMessage(errorMessage + ' ' + errorCode);
			});
	} 

	$scope.requisitionChange = function() {
		if ($scope.requisition != undefined) { 
			var url = '/api/x_tjc_easy_asset/easyasset/getpo?sys_id=' + $scope.requisition;
			$http.get(url).then(
				function(r) {
					toggle();
					//$('#ponumber').val(r.data.result);
					$scope.ponumber = r.data.result;
				}, function(e) {
				console.log(e);
				var errorMessage = 'Something went wrong!';
				var errorCode = '';

				if (e.status == 400) {errorCode = 'COAL'}
				if (e.status == 401) {errorCode = 'IRON'}
				if (e.status == 402) {errorCode = 'REDSTONE'}
				if (e.status == 403) {errorCode = 'DIAMONDS'}
				if (e.status == 404) {errorCode = 'VOID'}
				if (e.status == 405) {errorCode = 'LAPIS'}
				if (e.status == 406) {errorCode = 'SANDSTONE'}
				if (e.status == 408) {errorCode = 'GOLD'}
				if (e.status == 409) {errorCode = 'QUARTZ'}

				spUtil.addErrorMessage(errorMessage + ' ' + errorCode);
			});
		} else { toggle(); }
	}
}

sp_widget.script:
(function() {
	// Poplulate Requisition options
	data.requisitionOptions = [];
	var requisition = new GlideRecord('x_tjc_easy_asset_requisition');
	requisition.addQuery('active', 'true');
	requisition.addQuery('stage', 0);
	requisition.query();

	while (requisition.next()) {
		var o = {};
		o.display = requisition.getDisplayValue();
		o.value = requisition.getUniqueValue();
		data.requisitionOptions.push(o);
	}
})();
