sys_script_include.script:
var EasyAssetHelper = Class.create();
EasyAssetHelper.prototype = {
    initialize: function() {
    },

	update: function(sys_id, po, invoice_number) {
		var returnCode = {};
		try {
			returnCode.code = 'SUCCESS';
			returnCode.message = '';
			var ear = new GlideRecord('x_tjc_easy_asset_requisition');

			try {
				ear.get(sys_id);
				ear.po_number = po;
				ear.stage = 1;
				ear.update();
			}catch(e){
				returnCode.code = 'FIGHTING LION';
				returnCode.message = e;
			}

			var cmdb = new GlideRecord('cmdb');
			try {
				cmdb.addQuery('x_tjc_easy_asset_requisition_record', sys_id);
				cmdb.query();
				while (cmdb.next()) {
					cmdb.po_number = po;
					cmdb.invoice_number = invoice_number;
					cmdb.update();
				}
			}catch(e){
				returnCode.code = 'SUNSHOT';
				returnCode.message = e;
			}
		}catch(e){
			returnCode.code = 'DARCI';
			returnCode.message = e;
		}

		return returnCode;
	},

	getPO : function(sys_id) {
		var gr = new GlideRecord('x_tjc_easy_asset_requisition');
		gr.get(sys_id);
		return gr.po_number.toString();
	},

	createRecords : function(table, quantity, requisitionNumber) {
		var returnCode = {};
		returnCode.code = 'SUCCESS';
		returnCode.message = '';
		try {
			try {
				var rn = new GlideRecord('x_tjc_easy_asset_requisition');
				var rnSysID = '';
				if (rn.get('requisition_number', requisitionNumber)) {
					rnSysID = rn.getUniqueValue();
				} else {
					rn.newRecord();
					rnSysID = rn.getUniqueValue();
					rn.requisition_number = requisitionNumber;
					rn.insert();
				}

				Number(quantity);
				for (var i = 0; i < quantity; i++) {
					var gr = new GlideRecord(table);
					gr.newRecord();
					gr.x_tjc_easy_asset_requisition_record = rnSysID;
					gr.insert();
				}
			} catch(e) {
				returnCode.code = 'HARD LIGHT';
				returnCode.message = e;
			}
		} catch(e) {
			returnCode.code = 'VIGILANCE WING';
			returnCode.message = e;
		}
		return returnCode;
	},

    type: 'EasyAssetHelper'
};
